sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	// Add your code here
	var str = previous.title.toString();
	var tableNm = ['x_187055_clapfront_incident_tbl', 'x_187055_clapfront_alert_tbl', 'x_187055_clapfront_subticket_tbl', 'x_187055_clapfront_problem_tbl'];
	var grNum;
	var prefix = [];
	var grTbl;
	var grObj = [];
	var id = ['ticket_id', 'alert_id', 'subticket_id', 'prob_id'];
	var strNum = 0;

	for(var j = 0 ; j < tableNm.length ; j++ ){
		grTbl = new GlideRecord(tableNm[j]);
		grObj.push(grTbl);

		grNum = new GlideRecord('sys_number');
		grNum.addQuery('category', tableNm[j]);
		grNum.query();
		if(grNum.next()){
			prefix.push(grNum.prefix);
		}

	}

	for(var i = 0 ; i < prefix.length ; i++){
		if(str.indexOf(prefix[i]) > -1){
			strNum = str.substring(str.indexOf(prefix[i]),str.indexOf(prefix[i]) + 14);
			grObj[i].addQuery('number',strNum);
			grObj[i].query();
			if(grObj[i].next()){
				if(grObj[i].getValue('status') != 2){

					previous.setValue(id[i], grObj[i].getUniqueValue());
					previous.archive = true;
					previous.update();
					grObj[i].work_notes = previous.mail_body.toString();
					grObj[i].update();

				}
			}
		}
	}

})(current, previous);
