sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
/* Optional GlideRecord */ event) {

	// Add your code here
	//送信元アドレスの設定
	var grSender = new GlideRecord('x_187055_clapfront_mst_sender_address');
	var subject = ''; //件名

	//送信元アドレス
	var sender = '';
	var displayName = 'CLAP';
	var displayAddress = 'clapcenter-now-cs@ntt.com';
	var tempBody = ''; //チケット回答内容格納用
	var mailBody = ''; //メール本文内容
	var serviceType = '';

	//送信元アドレスマスタからチケット種別でフィルタして送信元アドレスを取得
	grSender.addQuery('ticket_type', '3');
	grSender.query();
	if(grSender.next()){
		//送信元アドレス表示名取得
		if(grSender.getValue('display_name'))
			displayName = grSender.getValue('display_name');
		//送信元アドレス取得
		if(grSender.getDisplayValue('sender') != '')
			displayAddress　= grSender.getDisplayValue('sender');
	}

	//送信元アドレス作成
	sender = displayName + '<' + displayAddress + '>';

	//サブチケットに紐づく上位チケットのサービス種別を取得
	if(current.getDisplayValue('ticket_id') != ''){ //チケットに紐付いている場合、
		serviceType = current.ticket_id.service_type.getDisplayValue();

	}else if(current.getDisplayValue('alert_id') != ''){ //アラートチケットに紐付いている場合、
		//アラートチケットの対応区分がアラートの場合、
		if(current.alert_id.support_type == 1){
			serviceType = current.alert_id.service_type_alt.getDisplayValue(); 

		//メンテナンスの場合
		}else if(current.alert_id.support_type == 2){
			serviceType = current.alert_id.service_type_maint.getDisplayValue(); 

		}
	}

	//件名の作成
	subject = '【' + current.getValue('number') + '】 ' + serviceType +  ' アサイン先組織変更通知';

	//メール本文の作成
	mailBody = current.short_description + '\n\n' + current.answer;

	//通知メールに設定
	email.setSubject(subject);
	email.setFrom(sender);
	email.setReplyTo(displayAddress);
	template.print(mailBody);

})(current, template, email, email_action, event);
