// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.

answer = checkTicketPause();

function checkTicketPause(){
	
	var pauseStart = current.task.pause_start;
	var pauseEnd = current.task.pause_end;
	var gdtPauseStart = new GlideDateTime(pauseStart);
	var gdtPauseEnd = new GlideDateTime(pauseEnd);
	var curTime = new GlideDateTime();
	
	if(curTime.after(gdtPauseStart) && curTime.before(gdtPauseEnd)){
		return 'yes';
	}else{
		return 'no';
	}
}

